import React from "react";

export default class Cores extends React.Component{
    constructor(param){
        //super(param);
        this.nome_cor = "Rosa";
        this.value = "#FF0084";
    };

    mudarCor= ()=>{
        let val, nome;
        val = "#005B8F";
        nome = "Azul";
        if (this.value === "#005B8F"){
            val = "#FF0084";
            nome = "Rosa"; 
        }
        this.nome_cor = nome;
        this.value = val;

        return;

    }

    component(){
        const cp = (
            <div>
                <h1 style={this.value}>{this.nome_cor}</h1>
                <button type="button" onClick={this.mudarCor}>Btn</button>
            </div>
        )
            return cp;
    }

    render(){
        return this.component;
    }
}
