import React from "react";

export default class Tabuada extends React.Component{

 Tabuada(n){
    
    const element = (
        <div>
        {creatH3(n)}
        </div>
    );
    return element;
};

    creatH3(n){
    let arr = [];
    arr.push(<h1 class= 'm-5'>Tabuada do {n}!</h1>);
    for (let index = 1; index < 11; index++) {
        const element = <h3>{index} X {n} = {index *n}</h3>;
        arr.push(element);
    }
    return arr;
};

    render(){
        return this.Tabuada(2);

    }
}
//export default class Tabuada;
