import React from "react";

class Cores extends React.Component{
    constructor(props){
        super(props);
        this.propriedades = {
            nome_cor : "Rosa",
            value : "#FF0084"
        }
       
    };

    mudarCor= ()=>{
        let val, nome;
        val = "#005B8F";
        nome = "Azul";
        if (this.value === "#005B8F"){
            val = "#FF0084";
            nome = "Rosa"; 
        }
        this.propriedades.nome_cor = nome;
        this.propriedades.value = val;

        return;

    }

    component(){
        const cp = (
            <div>
                <h1 style= {{color:this.propriedades.value}}>{this.propriedades.nome_cor}</h1>
                <button type="button" onClick={this.mudarCor}>Btn</button>
            </div>
        )
            return cp;
    }

    render(){
        return (<div>
            <h1 style={{color:this.propriedades.value}}>{this.propriedades.nome_cor}</h1>
            <button type="button" onClick={this.mudarCor}>Btn</button>
        </div>);
    }
}

export default Cores;
